INSERT INTO `ls_course_catalogue` (`id`, `course_id`, `name`, `fee_type`, `cover`, `content`, `duration`, `sort`, `status`, `create_time`, `update_time`, `delete_time`) VALUES (2, 2, '常量', 0, '', '<p>常量的概述</p><p>常量是指在程序运行过程中其值不能改变的量。</p><p>常量的类型</p><p>Java中常量的分类：</p><p><br></p><p> &nbsp; &nbsp;整数常量 ： 所有整数</p><p><br></p><p> &nbsp; &nbsp;小数常量 ： 所有小数</p><p><br></p><p> &nbsp; &nbsp;布尔常量 ： 只有true和false</p><p><br></p><p> &nbsp; &nbsp;字符常量 ：使用’’引起来的单个字符</p><p><br></p><p> &nbsp; &nbsp;字符串常量 ：使用“”引起来的字符序列，“” &nbsp;、“a” 、” ” </p><p><br></p><p> &nbsp; &nbsp;null常量 ： 只有一个值null</p>', 0, 0, 1, 1670406927, 1670406927, NULL);
